import G6 from '@antv/g6';
import { Item } from '@antv/g6/lib/types';
import { Group, IShape } from '@antv/g-canvas/lib';
import { PipelineGraphConfig, NodeStatus, pipelineNode, NodeRole, hasRightNeighborNode, hasSubNode } from './common';
import { INode } from '@antv/g6/lib/interface/item';


function drawPipeline(cfg: PipelineGraphConfig, group: Group): IShape {
  const shape = drawBase(cfg, group);
  drawNodeStatus(cfg, group);
  drawTime(cfg, group);
  return shape;
}

//drawBase draw base shape :4shape
function drawBase(cfg: PipelineGraphConfig, group: Group): any {
  const shape = group.addShape("rect", {
    attrs: {
      x: 2,
      y: 2,
      width: 180,
      height: 42,
      radius: 8,
      fill: "#f1f1f9",
    },
    name: "main-box",
    draggable: true,
  });

  // 标题
  group.addShape("text", {
    attrs: {
      y: 20,
      x: 5,
      height: 16,
      width: 16,
      text: cfg.taskName,
      style: {
        fontWeight: 900,
      },
      fill: "gray",
    },
    name: "title",
  });

  group.addShape("image", {
    attrs: {
      x: 140,
      y: 10,
      width: 25,
      height: 25,
      img: "data:image/png;base64,",
    },
    name: "image-shape",
  });

  return shape;
}

function drawStatus(group: Group, color: string, name: string): IShape {
  return group.addShape("circle", {
    attrs: {
      x: 20,
      y: 33,
      r: 3.5,
      fill: color,
    },
    name: name,
  });
}

function drawNodeStatus(cfg: PipelineGraphConfig, group: Group): IShape {
  switch (cfg.status) {
    case NodeStatus.Pending:
      drawStatus(group, "#ffc12f", "pendding");
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "Pendding.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Running:
      drawStatus(group, "#20d867", "running");
      //status for text
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "Running.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Progress:
      group.addShape("circle", {
        attrs: {
          x: 13,
          y: 32,
          r: 5,
          fill: "#ea9518",
        },
        name: "image-shape",
      });

      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "In progress.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Cancel:
      drawStatus(group, "#3296fa", "cancel");
      //status for text
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "Cancel.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Succeeded:
      group.addShape("image", {
        attrs: {
          x: 6,
          y: 25,
          width: 18,
          height: 18,
          img:
            "data:image/png;base64,",
        },
        name: "image-shape",
      });

      //status for text
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "Succeed.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Timeout:
      // set status
      drawStatus(group, "#f02b2b", "timeout");
      //status for text
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "TimeOut.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });

    case NodeStatus.Failed:
      group.addShape("image", {
        attrs: {
          x: 8,
          y: 25,
          width: 15,
          height: 15,
          img:
            "data:image/png;base64,",
        },
        name: "image-shape",
      });

      //status for text
      return group.addShape("text", {
        attrs: {
          y: 40,
          x: 25,
          height: 16,
          width: 16,
          text: "Failed.",
          fill: "gray",
          fontSize: 10,
        },
        name: "title",
      });
  }
}

function drawTime(cfg: PipelineGraphConfig, group: Group) {
  if (cfg.showtime) {
    //time for image
    group.addShape("image", {
      labels: "timeimage",
      attrs: {
        x: 90,
        y: 28,
        width: 15,
        height: 15,
        img:
          "data:image/png;base64,",
      },
      name: "image-shape",
    });
    //time for text
    return group.addShape("text", {
      labels: "time",
      attrs: {
        y: 42,
        x: 103,
        text: cfg.time ?? "0s",
        fill: "gray",
        fontSize: 10,
      },
      name: "title",
    });
  }
}


function drawAddNode(group: Group) {
  const color = "#bbfdec";
  group.addShape("circle", {
    name: "test",
    labels: "addond",
    attrs: {
      x: 192.5,
      y: 23,
      r: 10.3,
      stroke: color,
      fill: color,
      isCollapseShape: true,
    },
  });

  group.addShape("text", {
    name: "right-plus",
    labels: "addond",
    attrs: {
      x: 192.5,
      y: 23,
      width: 20,
      height: 20,
      textAlign: "center",
      textBaseline: "middle",
      text: "✚",
      fill: "#51863d",
      cursor: "pointer",
      isCollapseShape: true,
    },
  });
}


function drawSubNode(group: Group) {
  const color = "#f1978b";
  group.addShape("circle", {
    name: "left-circle",
    labels: "addond",
    attrs: {
      x: -9.1,
      y: 23,
      r: 10.3,
      stroke: color,
      fill: color,
      isCollapseShape: true,
    },
  });
  group.addShape("text", {
    name: "left-plus",
    labels: "addond",
    attrs: {
      x: -9.1,
      y: 23,
      width: 20,
      height: 20,
      textAlign: "center",
      textBaseline: "middle",
      text: "✂︎",
      fill: "#51863d",
      cursor: "pointer",
      isCollapseShape: true,
    },
  });
}


G6.registerNode(pipelineNode, {
  drawShape: function drawShape(cfg: PipelineGraphConfig, group: Group) {
    return drawPipeline(cfg, group);
  },

  // handle node event
  setState(name: string, value: string, item: Item) {
    const group = item.getContainer();

    if (name === "time" && value) {
      group.getChildren()
        .find(child => { return child.get("labels") == "time" })
        .attr({ text: value });
    }

    if (name === "hover" && value && item.getModel().role === NodeRole.Primary) {
      setTimeout(() => {
        drawAddNode(group);

        if (item.getID() == "1-1") {
          return;
        }

        if (hasRightNeighborNode(<INode>item)) {
          return;
        }

        if (hasSubNode(<INode>item)) {
          return;
        }

        drawSubNode(group);
      }, 100);
    }

    if (name === "hover" && value && item.getModel().role === NodeRole.Second) {
      setTimeout(() => {
        drawSubNode(group)
      }, 100);
    }

    if (name === "hover" && !value) {
      setTimeout(() => {
        group.getChildren()?.
          filter((child) => {
            return child.get("labels") === "addond"
          }).
          map((item) => {
            group.removeChild(item)
          })
      }, 300);
    }

    if (name === "click") {
      let shape = group.get("children")[2];
      shape.attr({ text: value });
    }

    if (name === "Running") {
      const shapestatus = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapestatus.attr({
        fill: "#20d867",
      });

      shapeText.attr({
        text: "Running. ",
        fontStyle: "",
      });
    }

    if (name === "Pendding") {
      const shapestatus = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapestatus.attr({
        fill: "#ffc12f",
      });

      shapeText.attr({
        text: "Pendding. ",
        fontStyle: "",
      });
    }

    if (name === "Succeeded") {
      const shapeImg = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapeImg.attr({
        img:
          "data:image/png;base64,",
      });

      shapeText.attr({
        text: "Succeed. ",
        fontStyle: "",
      });
    }

    if (name === "Failed") {
      const shapeImg = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapeImg.attr({
        img:
          "data:image/png;base64,",
      });
      shapeText.attr({
        text: "Failed. ",
        fontStyle: "",
      });
    }

    if (name === "TaskRunCancelled") {
      const shapestatus = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapestatus.attr({
        fill: "#3296fa",
      });

      shapeText.attr({
        text: "Cancel. ",
        fontStyle: "",
      });
    }

    if (name === "TaskRunTimeout") {
      const shapestatus = group.get("children")[4];
      const shapeText = group.get("children")[5];
      shapestatus.attr({
        fill: "#f02b2b",
      });

      shapeText.attr({
        text: "Cancel. ",
        fontStyle: "",
      });
    }
  },
}
);
